/**
 * This function removes all newline characters ("\n") from a given string.
 * In the context of your n8n workflow, you can use this in a "Code" node
 * to clean up the PDF text content before passing it to the AI Agent.
 *
 * @param {string} text The input string that may contain newline characters.
 * @returns {string} The processed string with all newline characters removed.
 */
function removeNewlines(text) {
  if (typeof text !== 'string') {
    // Return an empty string or handle the error as appropriate for your workflow
    console.error("Input must be a string.");
    return "";
  }
  // The .replace() method with a regular expression /g ensures all occurrences are replaced.
  return text.replace(/\n/g, ' ');
}

